!(function (o) {
  "use strict";
  o("body")
    .on("input propertychange", ".floating-label-form-group", function (i) {
      o(this).toggleClass(
        "floating-label-form-group-with-value",
        !!o(i.target).val()
      );
    })
    .on("focus", ".floating-label-form-group", function () {
      o(this).addClass("floating-label-form-group-with-focus");
    })
    .on("blur", ".floating-label-form-group", function () {
      o(this).removeClass("floating-label-form-group-with-focus");
    });
  if (992 < o(window).width()) {
    var s = o("#mainNav").height();
    o(window).on(
      "scroll",
      {
        previousTop: 0,
      },
      function () {
        var i = o(window).scrollTop();
        i < this.previousTop
          ? 0 < i && o("#mainNav").hasClass("is-fixed")
            ? o("#mainNav").addClass("is-visible")
            : o("#mainNav").removeClass("is-visible is-fixed")
          : i > this.previousTop &&
            (o("#mainNav").removeClass("is-visible"),
            s < i &&
              !o("#mainNav").hasClass("is-fixed") &&
              o("#mainNav").addClass("is-fixed")),
          (this.previousTop = i);
      }
    );
  }
})(jQuery);
/*Scroll to top when arrow up clicked BEGIN*/
$(window).scroll(function () {
  var height = $(window).scrollTop();
  if (height > 100) {
    $("#back2Top").fadeIn();
  } else {
    $("#back2Top").fadeOut();
  }
});
$(document).ready(function () {
  $("#back2Top").click(function (event) {
    event.preventDefault();
    $("html, body").animate(
      {
        scrollTop: 0,
      },
      "slow"
    );
    return false;
  });
});
/*Scroll to top when arrow up clicked END*/
$(document).ready(function () {
    // Function to filter content based on input or class
    function filterContent(value) {
        $("#myDIV .post-preview").filter(function () {
            $(this).toggle($(this).hasClass(value));
        });
    }

    // Function to filter content based on search input
    function filterBySearch(value) {
        $("#myDIV .post-preview").filter(function () {
            $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1);
        });
    }

    // Event handler for search input
    $("#myInput").on("keyup", function () {
        var value = $(this).val().toLowerCase();
        filterBySearch(value);
    });

    // Event handlers for filter buttons
    $("#filter-books-btn").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('books'); // Filter for books
    });

    $("#filter-wine-btn").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('wine');  // Filter for wine
    });

    $("#filter-food-btn").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('food');  // Filter for food
    });

    $("#filter-all").on("click", function () {
        $("#myInput").val("");  // Clear search input
        $("#myDIV .post-preview").show(); // Show all reviews
    });

    // Event handlers for dropdown menu items
    $("#dropdown-filter-books").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('books'); // Filter for books
    });

    $("#dropdown-filter-wine").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('wine');  // Filter for wine
    });

    $("#dropdown-filter-food").on("click", function () {
        $("#myInput").val("");  // Clear search input
        filterContent('food');  // Filter for food
    });
});
